import React from 'react';
import { ImageBackground, StyleSheet, Button, Text, View } from 'react-native';
import DropDownPicker from 'react-native-dropdown-picker';

const imgPolitica = require("./imagini/truePolitics.jpg")

export default class App extends React.Component {

  constructor(props){
    super(props)

    this.state = {
      open : false,
      value : null,
      items : [{
        label:'Dorb',
        value: 'dorb',
      },
      {
      label : 'Danela',
      value : 'danela',
      }],
    }

    this.setOpen = this.setOpen.bind(this)
    this.setValue = this.setValue.bind(this)
    this.setItems = this.setItems.bind(this)
  }


  setOpen(open){
    console.log("am intrat aici")
    this.setState({
      open
    })
  }

  setValue(callback)
  {
    this.setState(state => ({
      value: callback(state.value)
    }))
  }

  setItems(callback)
  {
    this.setState(state => ({
      items: callback(state.items)
    }))
  }



  render()
  {
    const {open, value, items} = this.state

    return (
      <View style={{flex:1}}>
        <View style={styles.topViewContainer}>
          <Text style={styles.text}> Alegeti-ne, sa fim vocea voastra</Text>
        </View>
        <View style={styles.motivatieTextContainer}>
          <ImageBackground source={imgPolitica} resizeMode="cover" style={styles.image}>
              <Text> motivatie </Text>
          </ImageBackground>
        </View>
      </View>
    )
  }
}

const styles = StyleSheet.create({
  topViewContainer: {
    position: 'absolute',
    right: '0%',
    width:'90%',
    height: '5%',
    top:'0%',
  },
  meniuStangaContainer: {
    flex : 1
  },
  motivatieTextContainer: {
    position: 'absolute',
    right: '0%',
    width:'900%',
    height: '100%',
    top:'0%',
    textAlign: 'center',
  },
  image: {
    position: 'absolute',
    height: '100%',
    width: '100%',
    top: '0%',
    left:'0%',
    justifyContent: "center"
  },
  text:{
    color: "#751600",
    fontSize: 42,
    lineHeight: 84,
    fontWeight: "bold",
    textAlign: "center",
    backgroundColor: "#fff"
  }
});
