import React from 'react';
import { ImageBackground, StyleSheet, Button, Text, View } from 'react-native';
import DropDownPicker from 'react-native-dropdown-picker';

const imgPolitica = require("./imagini/truePolitics.jpg")

export default class App extends React.Component {

  constructor(props){
    super(props)

    this.state = {
      open : false,
      value : null,
      items : [{
        label:'Dorb',
        value: 'dorb',
      },
      {
      label : 'Danela',
      value : 'danela',
      }],
    }

    this.setOpen = this.setOpen.bind(this)
    this.setValue = this.setValue.bind(this)
    this.setItems = this.setItems.bind(this)
  }


  setOpen(open){
    console.log("am intrat aici")
    this.setState({
      open
    })
  }

  setValue(callback)
  {
    this.setState(state => ({
      value: callback(state.value)
    }))
  }

  setItems(callback)
  {
    this.setState(state => ({
      items: callback(state.items)
    }))
  }



  render()
  {
    const {open, value, items} = this.state

    return (
      <View style={styles.container}>
        <ImageBackground source={imgPolitica} resizeMode="cover" style={styles.image}>
          <View style = {styles.topViewContainer}>
            <View style={styles.motivatieTextContainer}>
              <Text> Alegeti-ne, sa fim vocea voastra</Text>
            </View>
          </View>
            <View style={styles.meniuStangaContainer}>
              <DropDownPicker
                open={open}
                value={value}
                items={items}
                setOpen={this.setOpen}
                setValue={this.setValue}
                setItems={this.setItems}
                dropDownDirection="BOTTOM"
                placeholder="despre noi"

              />
              <Button title="Proiecte" />
              <Button title="Contact"/>
            </View>
            <View>
              <Text> motivatie </Text>
            </View>
        </ImageBackground>
      </View>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
    justifyContent: 'center',
    height : '80%',
    width : '80%',
    position: 'absolute',
  },
  topViewContainer: {
    flex: 1,
    height: '20%',
    width : '100%',
  },
  meniuStangaContainer: {
    flex : 1
  },
  motivatieTextContainer: {
    alignItems: 'center',
    justifyContent: 'center',
    position: 'absolute',
    right: '45%',
    padding: 20,
    textAlign: 'center',
  },
  image: {
    flex: 1,
    justifyContent: "center"
  },
});
